mapper.select:
SELECT intSeq, strEmail, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, dateReg, dateEdit, intCompanySeq, intSuper FROM TB_CLIENT WHERE intSeq > 0
AND intStatus = #{status}
AND (strEmail LIKE CONCAT('%', #{keyword}, '%') OR strMobile LIKE CONCAT('%', #{keyword}, '%') OR strName LIKE CONCAT('%', #{keyword}, '%') OR intSeq = (SELECT a.intUserSeq FROM TB_TAG_LINK a, TB_TAGS b WHERE b.intSeq = a.intTagSeq AND a.intUserType = 2 AND b.strTag LIKE CONCAT('%', #{keyword}, '%')) OR intCompanySeq = (SELECT intSeq FROM TB_COMPANY WHERE strCompanyname LIKE CONCAT('%', #{keyword}, '%')))
ORDER BY intSeq DESC
LIMIT #{limit}, #{length} ;

SELECT COUNT(intSeq) FROM TB_CLIENT WHERE intSeq > 0
AND intStatus = #{status}
AND (strEmail LIKE CONCAT('%', #{keyword}, '%') OR strMobile LIKE CONCAT('%', #{keyword}, '%') OR strName LIKE CONCAT('%', #{keyword}, '%') OR intSeq = (SELECT a.intUserSeq FROM TB_TAG_LINK a, TB_TAGS b WHERE b.intSeq = a.intTagSeq AND a.intUserType = 2 AND b.strTag LIKE CONCAT('%', #{keyword}, '%')) OR intCompanySeq = (SELECT intSeq FROM TB_COMPANY WHERE strCompanyname LIKE CONCAT('%', #{keyword}, '%'))) ;

SELECT COUNT(intSeq) FROM TB_ADMIN WHERE intSeq > 0
AND intStatus = #{status}
AND (strEmail LIKE CONCAT('%', #{keyword}, '%') OR strName LIKE CONCAT('%', #{keyword}, '%')) ;

mapper.delete:
DELETE
FROM TB_WORK
WHERE
intSeq > 0

    AND intSeq = #{intSeq}


    AND intClientSeq = #{intClientSeq}


    AND intCompanySeq = #{intCompanySeq}
;

mapper.select:
SELECT a.intSeq, a.strTitle, a.txtInfo, a.dateReadystart, a.dateReadyend, a.dateStart, a.dateEnd, a.intClientSeq, a.intCompanySeq, a.intSelecttype, a.intTotalPrice, a.intStatus, a.dateReg, a.dateEdit, b.strName as strClientName, b.strEmail as strClientEmail, b.strMobile as strClientMobile, c.strCompanyname FROM TB_WORK a, TB_CLIENT b, TB_COMPANY c WHERE a.intClientSeq = b.intSeq AND a.intCompanySeq = c.intSeq
AND a.intStatus = #{status}
AND (a.strTitle LIKE CONCAT('%', #{keyword}, '%') OR a.txtInfo LIKE CONCAT('%', #{keyword}, '%') OR a.intSeq = (SELECT aa.intWorkSeq FROM TB_TAG_LINK aa, TB_TAGS bb WHERE bb.intSeq = aa.intTagSeq AND aa.intUserType = 5 AND bb.strTag LIKE CONCAT('%', #{keyword}, '%')) OR b.strName LIKE CONCAT('%', #{keyword}, '%') OR c.strCompanyname LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY a.intSeq DESC
LIMIT #{limit}, #{length} ;

SELECT COUNT(a.intSeq) FROM TB_WORK a, TB_CLIENT b, TB_COMPANY c WHERE a.intClientSeq = b.intSeq AND a.intCompanySeq = c.intSeq
AND a.intStatus = #{status}
AND (strTitle LIKE CONCAT('%', #{keyword}, '%') OR txtInfo LIKE CONCAT('%', #{keyword}, '%') OR a.intSeq = (SELECT aa.intWorkSeq FROM TB_TAG_LINK aa, TB_TAGS bb WHERE bb.intSeq = aa.intTagSeq AND aa.intUserType = 5 AND bb.strTag LIKE CONCAT('%', #{keyword}, '%')) OR b.strName LIKE CONCAT('%', #{keyword}, '%') OR c.strCompanyname LIKE CONCAT('%', #{keyword}, '%')) ;

mapper.delete:
DELETE
FROM TB_WORK_INFO
WHERE
intSeq > 0

    AND intSeq = #{intSeq}


    AND intWorkSeq = #{intWorkSeq}


    AND intClientSeq = #{intClientSeq}
;

mapper.select:
SELECT
intSeq, intWorkSeq, intClientSeq, intInfSeq, intMediaType, intMediaSeq, strURL, intUserStatus,
intClientStatus, intPrice, dateReg, dateEdit, intStatus
FROM TB_WORK_INFO
WHERE
    intSeq > 0

    AND intSeq = #{intSeq}


    AND intWorkSeq = #{intWorkSeq}


    AND intClientSeq = #{intClientSeq}

ORDER BY intSeq ASC;
