mapper.select:
SELECT intSeq, strEmail, strPassword, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, dateReg, dateEdit, intCompanySeq, intSuper
, PASSWORD(#{strPassword}) as strPassword2
FROM TB_INFUENCER WHERE
strEmail = #{strEmail}
intSeq = #{intSeq} ;

SELECT intSeq, strEmail, strPassword, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, dateReg, dateEdit, intCompanySeq, intSuper
, PASSWORD(#{strPassword}) as strPassword2
FROM TB_CLIENT WHERE
strEmail = #{strEmail}
intSeq = #{intSeq} ;

SELECT intSeq, strName, strEmail, strPassword, intLevel, intStatus, dateReg, dateEdit, txtPermission, intSuper
, PASSWORD(#{strPassword}) as strPassword2
FROM TB_ADMIN WHERE
strEmail = #{strEmail}
intSeq = #{intSeq} ;

SELECT intSeq, strCompanyname, strHomepage, intLevel, strTelnum, strZipcode, strAddress, strZone, intStatus, dateReg, dateEdit, strCompanynum FROM TB_COMPANY WHERE intSeq > 0
AND intSeq = #{intSeq}
AND strCompanyname = #{strCompanyname} ;

SELECT
intSeq, intUserSeq, intUserType, intType, strURL, intStatus
FROM
TB_MEDIA
WHERE
intSeq > 0

    AND intSeq = #{intSeq}


    AND intUserType = #{intUserType}


    AND intUserSeq = #{intUserSeq}


    AND strURL = #{strURL}
;

mapper.update:
UPDATE TB_MEDIA SET

    strURL = #{strURL},


    intStatus = #{intStatus},

dateEdit = CURRENT_TIMESTAMP
WHERE
intSeq > 0

    AND intSeq = #{intSeq}


    AND intUserType = #{intUserType}


    AND intUserSeq = #{intUserSeq}
;

mapper.delete:
DELETE FROM TB_MEDIA
WHERE
intSeq > 0

    AND intSeq = #{intSeq}


    AND intUserType = #{intUserType}


    AND intUserSeq = #{intUserSeq}
;

mapper.select:
SELECT intSeq, strTag FROM TB_TAGS
WHERE
intSeq > 0

    AND strTag = #{strTag}
;

SELECT a.strTag FROM TB_TAGS a, TB_TAG_LINK b
WHERE
a.intSeq = b.intTagSeq

    AND b.intUserType = #{intUserType}


    AND b.intUserSeq = #{intUserSeq}


    AND a.strTag = #{strTag}
;

mapper.delete:
DELETE
FROM TB_TAG_LINK
WHERE
intSeq > 0

    AND intUserType = #{intUserType}


    AND intUserSeq = #{intUserSeq}


    AND intTagSeq = #{intTagSeq}
;

DELETE
FROM TB_CASH
WHERE
intSeq > 0

    AND intUserType = #{intUserType}


    AND intUserSeq = #{intUserSeq}


    AND intSeq = #{intSeq}
;

mapper.select:
SELECT
intSeq, intUserType, intUserSeq, intCashType, strBankType, strBankNum
FROM
TB_CASH
WHERE
intSeq > 0

    AND intSeq = #{intSeq}


    AND intUserType = #{intUserType}


    AND intUserSeq = #{intUserSeq}
;

SELECT intSeq, strEmail, strPassword, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, dateReg, dateEdit, intCompanySeq, intSuper FROM TB_INFUENCER WHERE intSeq > 0
AND intStatus = #{status}
AND (strEmail LIKE CONCAT('%', #{keyword}, '%') OR strMobile LIKE CONCAT('%', #{keyword}, '%') OR strName LIKE CONCAT('%', #{keyword}, '%') OR strNikname LIKE CONCAT('%', #{keyword}, '%') OR intSeq = (SELECT a.intUserSeq FROM TB_TAG_LINK a, TB_TAGS b WHERE b.intSeq = a.intTagSeq AND a.intUserType = 2 AND b.strTag LIKE CONCAT('%', '테', '%')) OR intCompanySeq = (SELECT intSeq FROM TB_COMPANY WHERE strCompanyname LIKE CONCAT('%', #{keyword}, '%')))
ORDER BY intSeq DESC
LIMIT #{limit}, #{length} ;

SELECT intSeq, strEmail, strPassword, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, dateReg, dateEdit, intCompanySeq, intSuper FROM TB_CLIENT WHERE intSeq > 0
AND intStatus = #{status}
AND (strEmail LIKE CONCAT('%', #{keyword}, '%') OR strMobile LIKE CONCAT('%', #{keyword}, '%') OR strName LIKE CONCAT('%', #{keyword}, '%') OR intSeq = (SELECT a.intUserSeq FROM TB_TAG_LINK a, TB_TAGS b WHERE b.intSeq = a.intTagSeq AND a.intUserType = 2 AND b.strTag LIKE CONCAT('%', '테', '%')) OR intCompanySeq = (SELECT intSeq FROM TB_COMPANY WHERE strCompanyname LIKE CONCAT('%', #{keyword}, '%')))
ORDER BY intSeq DESC
LIMIT #{limit}, #{length} ;

SELECT intSeq, strName, strEmail, strPassword, intLevel, intStatus, dateReg, dateEdit, txtPermission, intSuper FROM TB_ADMIN WHERE intSeq > 0
AND intStatus = #{status}
AND (strEmail LIKE CONCAT('%', #{keyword}, '%') OR strName LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY intSeq DESC
LIMIT #{limit}, #{length} ;
