SELECT intSeq, strEmail, strPassword, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, strInstargram, strYoutube, strBlog, strMall, dateReg, dateEdit, intCompanySeq
, PASSWORD(#{strPassword}) as strPassword2
FROM TB_INFUENCER WHERE strEmail = #{strEmail}

SELECT intSeq, strEmail, strPassword, strName, strMobile, strNikname, intSex, txtPermission, intLevel, intStatus, dateReg, dateEdit, intCompanySeq
, PASSWORD(#{strPassword}) as strPassword2
FROM TB_CLIENT WHERE strEmail = #{strEmail}

SELECT intSeq, strName, strEmail, strPassword, intLevel, intStatus, dateReg, dateEdit, txtPermission
, PASSWORD(#{strPassword}) as strPassword2
FROM TB_ADMIN WHERE strEmail = #{strEmail}
