mapper.select:
SELECT dateDay,
       intInfluencer,
       intClient
FROM TB_USERS
WHERE dateDay BETWEEN #{start} AND #{end}
ORDER BY dateDay ASC;

SELECT intSeq, intCode, intAdminSeq, intClientSeq, intInfluencerSeq, intWorkSeq, intWorkSeq, txtMemo, dateReg
FROM TB_HISTORY
WHERE
intSeq > 0

    AND intAdminSeq = #{intAdminSeq}


    AND intClientSeq = #{intClientSeq}


    AND intInfluencerSeq = #{intInfluencerSeq}


    AND intWorkSeq = #{intWorkSeq}

mapper.insert:
INSERT INTO TB_WORK_${intSeq}_LOG (
    dateDay, intMediaSeq, intMediaType, intClientSeq, intInfluencerSeq, intCount1, intCount2, intCount3, intCount4, intCount5, intCount6, intCount7, intCount8, intCount9, intCount10
) VALUES (
    #{dateDay}, #{intMediaSeq}, #{intMediaType}, #{intClientSeq}, #{intInfluencerSeq}, #{intCount1}, #{intCount2}, #{intCount3}, #{intCount4}, #{intCount5}, #{intCount6}, #{intCount7}, #{intCount8}, #{intCount9}, #{intCount10}
};

mapper.update:
UPDATE TB_WORK_${intSeq}_LOG SET dateDay = #{}, intMediaSeq, intMediaType, intClientSeq, intInfluencerSeq, intCount1, intCount2, intCount3, intCount4, intCount5, intCount6, intCount7, intCount8, intCount9, intCount10 WHERE intSeq > 0
AND intSeq = #{intSeq}
AND intMediaSeq = #{intMediaSeq}
AND intMediaType = #{intMediaType}
AND intClientSeq = #{intClientSeq}
AND intInfluencerSeq = #{intInfluencerSeq} ;
